jQuery.retryAjax=function(a){if(!a.timeout)a.timeout=1e4;if(!a.tryCount)a.tryCount=0;if(!a.retryLimit)a.retryLimit=2;a.complete=function(a,b){if($.inArray(b,["timeout","abort","error"])>-1){this.tryCount++;if(this.tryCount<=this.retryLimit){$.ajax(this);return true}alert("There was a server error.  Please refresh the page.  If the issue persists, give us a call. Thanks!");return true}};$.ajax(a)}